import React from "react";
import axios from "axios";
import Articles from "../components/Business";


class BusinessList extends React.Component {
    state = {
        businesses: []
    };

    fetchArticles = () => {
        axios.get("http://127.0.0.1:8000/api/").then(res => {
            this.setState({
                businesses: res.data
            });
        });
    }

    componentDidMount() {
        this.fetchArticles();
    }

    componentWillReceiveProps(newProps) {
        if (newProps.token) {
            this.fetchArticles();
        }
    }

    render() {
        return (
            <div>
                <Articles data={this.state.businesses} /> <br />
                <h2> Create an article </h2>
            </div>
        );
    }
}

export default BusinessList;
